import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/practice.css';
import { getFeedback } from '../api/chat_api';

export default function Practice() {
    const { getTopic, getQuestion } = useParams();
    const [answerInput, setAnswerInput] = useState('');
    const [feedback, setFeedback] = useState('');
    const [title, setGptTitle] = useState('');
    const [question, setGptQuestion] = useState('');
    const [userAnswer, setGptUserAnswer] = useState('');

    const decodedTopic = decodeURIComponent(topic);
    const decodedQuestion = decodeURIComponent(question);

    const handleFeedback = async () => {

        if (answerInput.trim() !== '') {
            setGptTitle("주제: " + decodeURIComponent(topic) + "\n");
            setGptQuestion("친구: " + decodeURIComponent(question) + "\n");
            setGptUserAnswer("사용자: " + answerInput);

            console.log(gptTitle, gptQuestion, gptUserAnswer);
            const response = await getFeedback({ title, question, userAnswer });
            if (response.status === 200) {
                console.log(response.data);
                setFeedback(response.data);
            } else {
                alert("error");
            }
        }
    }

    return (
        <div className="practice-container">
            <div>{decodedTopic}</div>
            <div className="question">{decodedQuestion}</div>
            <div className="answer">{answerInput}</div>
            <div className="feecback">feedback</div>
            <input onChange={(event) => setAnswerInput(event.target.value)}></input>
            <button onClick={handleFeedback}>전송</button>
        </div>
    )
}
